  filter(year == params$year)|&gt;
  mutate(pct_fmv_exempt = muni_fmv_exempt / muni_fmv ) |&gt;
    mutate(agency_name = ifelse(agency_name == "TOWN CICERO", "CITY OF CICERO", agency_name) ) |&gt;
