#read_csv(paste0("../../Output/Dont_Upload/0_Joined_PIN_data_", params$year, ".csv") )

pin_data &lt;- read_csv(paste0("../Output/Dont_Upload/0_joined_PIN_data_", params$year, ".csv"))


mc_burden &lt;- read_csv(paste0("../Output/muni_mc_burden_", params$year, ".csv"))


burden_shift &lt;- read_csv(paste0("../Output/muni_burden_shift_", params$year, ".csv"))




Code
mc_burden |&gt; 
  mutate(group = case_when(class_1dig == 2 ~ "Class 2",
                         class_1dig == 3 ~ "Class 3",
                         T ~ "All Other Classes"))|&gt;

  select(clean_name, group,  
         pct_taxburden_current, hyp_pct_taxburden) |&gt;
  group_by(clean_name, group) |&gt;
  summarize(`Current Tax Burden Share` = sum(pct_taxburden_current),
           `Tax Burden Share without Exemptions` =sum(hyp_pct_taxburden)) |&gt;
  filter(clean_name=="Dolton") |&gt;
  pivot_longer(cols = c(`Current Tax Burden Share`, `Tax Burden Share without Exemptions`), 
                names_to = "type", values_to = "value") |&gt;
  ggplot(aes(x = group, y= value, group = type, fill = type) )+ 
  geom_col(position = "dodge") +  
  geom_text(aes(label = scales::percent(round(value, digits=3))), 
              vjust=-0.5, position = position_dodge(.9)) + 
  theme_classic()+
  theme(legend.title = element_blank(), legend.position = "bottom",
        axis.title.x  = element_blank()) + 
  scale_y_continuous(label = scales::percent, name = "Share of Levy")











Code
burden_c2 &lt;- mc_burden |&gt; 
  filter(class_1dig == 2) |&gt; 
  select(clean_name, #pct_taxbase, 
         burden_shift,  pct_taxburden_current, hyp_pct_taxburden)

burden_shift |&gt;  
  select(-c(muni_levy, muni_taxbase,))|&gt;
  select(clean_name, Group, pct_taxburden_current, hyp_pct_taxburden, everything()) |&gt;
  DT::datatable(rownames = FALSE, 
                colnames = c(
                  'Municipality' = 'clean_name',
                  'Current Taxbase' = 'group_taxbase', 
                  'Hyp. Taxbase' = 'hyp_group_taxbase',  
                  'Taxes Paid'='group_taxes_current', 
                  'Hyp Taxes Paid' = 'hyp_group_taxes',
                  'Share of Levy'='pct_taxburden_current',
                  'Share of Taxbase' = 'pct_taxbase_current',
              "Hyp. Share of Levy"  =  'hyp_pct_taxburden',
              "Burden Shift" = 'burden_shift'
                  ),
                caption = "Table 2 in Report for all Municipalities: Current and Hypothetical Composite Tax Rates if GHE $0"
      ) |&gt;
  formatCurrency(columns = c(5:8), digits = 0) |&gt;
  formatPercentage(columns = c(3,4,9,10), digits = 2)








Code
mc_burden |&gt; 
    mutate(pct_taxburden_current = round(pct_taxburden_current * 100, digits = 2),
