  filter(clean_name == "Dolton") |&gt; 
  select(`Major Class`= class_1dig, `Current Tax Burden` = pct_taxburden_current, `Alt. Burden` = hyp_pct_taxburden) |&gt; 
  arrange(desc(`Current Tax Burden`)) |&gt;
  flextable::flextable()




