burden_c2 |&gt; 
  mutate(burden_shift = burden_shift*100) |&gt; # multiply by 100 to make it percentage point change without formatting to percent below.
  datatable(rownames = FALSE,
          colnames = c('Municipality' = 'clean_name', 
                       'Burden Shift, Pct Pt Change' = 'burden_shift', 
