muni_sums |&gt; 
  filter(year == params$year) |&gt;
  group_by(clean_name) |&gt; 
  summarize(pct_res_exempt = muni_fmv_exempt / muni_fmv_residential) |&gt;  
  arrange(desc(pct_res_exempt) ) |&gt;
    left_join(C2_munistats_filtered) |&gt; 
