         pct_res_exempt = scales::percent(pct_res_exempt, accuracy = 0.01) ) |&gt;
  mutate(across(c(median_eq_av:avg_taxed_eav, prop_value), scales::dollar)) |&gt;
  select(Municipality = clean_name, `Share Res. Exempt` = pct_res_exempt,
         `Median Prop. Value` = prop_value, everything())









